import { useState } from "react"
const pic = require("../images/main_pic(cropped).jpg")

const Content = (props) => {
    const [picSource, setPicSource] = useState(pic)
    const [text, setText] = useState("This text will be state controlled!")

    return (

        <div className="col-6 d-flex align-items-center justify-content-center">
            <div className="card">
            {
                        props.text === "Resume" ? 
                            <div className="ratio ratio-1x1">
                                <iframe title="resume" src="https://docs.google.com/document/d/e/2PACX-1vSF-PY5ZpCgx5m1llmkQHSp62y0NO_ho2B22Dh-DY2K1E90q9vR4Dp9ztoZvViAhw/pub?embedded=true"></iframe>
                            </div>
                            :
                            <>
                                <img className='card-img-top rounded' src={props.pic} style={{maxWidth: "400px", maxHeight: "auto" }} alt='main' />
                                <div className="card-body">
                                    <p className="card-text text-center">{props.text}</p>
                                </div>
                            </>
            }
            </div>
        </div>
    )
}

export default Content
